mapper.insert:
INSERT INTO TB_TOKEN (
    USER_ID
    , TOKEN_TYPE
    , TOKEN
    , REG_DT
)
VALUES (
    #{userId}
    , #{tokenType}
    , #{token}
    , NOW()
)

mapper.update:
UPDATE TB_TOKEN
SET TOKEN = #{token}
    , UPD_DT = NOW()
WHERE TOKEN_ID = #{tokenId}

mapper.select:
SELECT *
FROM TB_TOKEN
WHERE USER_ID = #{userId}
AND TOKEN_TYPE = #{tokenType}

SELECT *
FROM TB_TOKEN
WHERE USER_ID = #{userId}
AND TOKEN_TYPE = #{tokenType}

SELECT *
FROM TB_TOKEN
WHERE USER_ID = #{userId}
AND TOKEN_TYPE = #{tokenType}
AND TOKEN = #{token}

SELECT
    TOKEN
FROM TB_TOKEN
WHERE TOKEN_TYPE = #{tokenType}
AND USER_ID IN

    #{userId}

mapper.delete:
	DELETE FROM TB_TOKEN
    WHERE TOKEN_TYPE = #{tokenType}
    AND TOKEN = #{token}
